export const NepaliPoemsGallery = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [selectedPoem, setSelectedPoem] = useState<Poem | null>(null);
  const poemsPerPage = 6;
  const totalPages = Math.ceil(nepaliPoems.length / poemsPerPage);

  const currentPoems = nepaliPoems.slice(
    currentPage * poemsPerPage,
    (currentPage + 1) * poemsPerPage
  );

  const nextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(prev => prev + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <motion.div 
        className="text-center"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <motion.div
          className="inline-flex items-center gap-2 mb-3"
          animate={{ y: [0, -5, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          <Book className="w-6 h-6 text-rose-400" />
          <Sparkles className="w-5 h-5 text-pink-400" />
          <Heart className="w-6 h-6 text-red-400 fill-red-400" />
        </motion.div>
        <h2 className="text-2xl md:text-3xl font-serif text-transparent bg-clip-text bg-gradient-to-r from-rose-300 via-pink-300 to-purple-300">
          ‡§®‡•á‡§™‡§æ‡§≤‡•Ä ‡§™‡•ç‡§∞‡•á‡§Æ ‡§ï‡§µ‡§ø‡§§‡§æ
        </h2>
        <p className="text-rose-200/60 mt-2 text-sm">
          100 love poems for my Puntuu üíï
        </p>
      </motion.div>

      {/* Poems Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        <AnimatePresence mode="popLayout">
          {currentPoems.map((poem, index) => (
            <motion.div
              key={poem.id}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ delay: index * 0.1 }}
              onClick={() => setSelectedPoem(poem)}
              className="cursor-pointer"
            >
              <div className="backdrop-blur-xl bg-white/10 border border-white/20 rounded-xl p-4 hover:bg-white/15 transition-all group">
                <div className="flex items-start gap-3">
                  <motion.span 
                    className="text-3xl"
                    animate={{ rotate: [0, 5, -5, 0] }}
                    transition={{ duration: 3, repeat: Infinity }}
                  >
                    {poem.emoji}
                  </motion.span>
                  <div className="flex-1">
                    <h3 className="text-rose-200 font-medium text-lg mb-1 group-hover:text-rose-100 transition-colors">
                      {poem.title}
                    </h3>
                    <p className="text-white/70 text-sm line-clamp-2 whitespace-pre-line">
                      {poem.content}
                    </p>
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {/* Pagination */}
      <div className="flex items-center justify-center gap-4">
        <motion.button
          onClick={prevPage}
          disabled={currentPage === 0}
          className={`p-3 rounded-full ${
            currentPage === 0
              ? "bg-white/5 text-white/30"
              : "bg-white/15 text-white hover:bg-white/25"
          }`}
          whileHover={currentPage > 0 ? { scale: 1.1 } : {}}
          whileTap={currentPage > 0 ? { scale: 0.9 } : {}}
        >
          <ChevronLeft className="w-5 h-5" />
        </motion.button>
        
        <div className="flex items-center gap-2">
          <span className="text-white text-sm">
            Page {currentPage + 1} of {totalPages}
          </span>
        </div>
        
        <motion.button
          onClick={nextPage}
          disabled={currentPage === totalPages - 1}
          className={`p-3 rounded-full ${
            currentPage === totalPages - 1
              ? "bg-white/5 text-white/30"
              : "bg-white/15 text-white hover:bg-white/25"
          }`}
          whileHover={currentPage < totalPages - 1 ? { scale: 1.1 } : {}}
          whileTap={currentPage < totalPages - 1 ? { scale: 0.9 } : {}}
        >
          <ChevronRight className="w-5 h-5" />
        </motion.button>
      </div>

      {/* Poem count indicator */}
      <div className="text-center">
        <p className="text-rose-300/50 text-xs">
          Showing poems {currentPage * poemsPerPage + 1} - {Math.min((currentPage + 1) * poemsPerPage, nepaliPoems.length)} of {nepaliPoems.length}
        </p>
      </div>

      {/* Selected Poem Modal */}
      <AnimatePresence>
        {selectedPoem && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-sm"
            onClick={() => setSelectedPoem(null)}
          >
            <motion.div
              initial={{ scale: 0.9, y: 20 }}
              animate={{ scale: 1, y: 0 }}
              exit={{ scale: 0.9, y: 20 }}
              onClick={(e) => e.stopPropagation()}
              className="bg-gradient-to-br from-rose-900/90 to-purple-900/90 border border-white/20 rounded-2xl p-6 max-w-md w-full max-h-[80vh] overflow-y-auto"
            >
              <div className="text-center mb-4">
                <motion.span 
                  className="text-5xl block mb-3"
                  animate={{ scale: [1, 1.1, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  {selectedPoem.emoji}
                </motion.span>
                <h3 className="text-2xl font-serif text-rose-200">
                  {selectedPoem.title}
                </h3>
              </div>
              
              <p className="text-white/90 font-serif text-lg leading-relaxed whitespace-pre-line text-center">
                {selectedPoem.content}
              </p>
              
              <div className="mt-6 flex justify-center">
                <motion.button
                  onClick={() => setSelectedPoem(null)}
                  className="px-6 py-2 bg-white/10 rounded-full text-white hover:bg-white/20 transition-colors"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Close
                </motion.button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};
